
public class PositiveAndNegetive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a1 = 5;
		findingno(a1);

	}

	static void findingno(int a) {
		if (a > 0) {
			System.out.println("the no is positive");
		} else {
			System.out.println("the no is negetive");
		}

	}

}
